package main.java.blockchain;

import com.fasterxml.jackson.databind.JsonNode;
import com.fasterxml.jackson.databind.ObjectMapper;
import main.java.crypto_utils.RSAKeyReader;
import main.java.utils.DataUtils;
import org.apache.tuweni.bytes.Bytes;
import org.hyperledger.besu.datatypes.Address;
import org.hyperledger.besu.evm.fluent.SimpleWorld;
import org.slf4j.Logger;
import org.slf4j.LoggerFactory;

import java.io.FileOutputStream;
import java.io.IOException;
import java.security.PublicKey;
import java.util.*;

public class GenesisBlockGenerator {
    private static final Logger logger = LoggerFactory.getLogger(GenesisBlockGenerator.class);

    private static final String publicKeysDir = "public_keys/";
    private static final String genesisBlockPath = "genesis_block.json";

    /**
     * AccessControl.sol bytecode from Remix->data->bytecode->object since they will be deployed using EVM
     */
    private static final Bytes accessControlBytecode = Bytes.fromHexString("");

    /**
     * ISTCoin.sol bytecode from Remix->data->bytecode->object since they will be deployed using EVM
     */
    private static final Bytes fungibleTokenBytecode = Bytes.fromHexString("");

    public static void main(String[] args) throws IOException {
        if (args.length != 1) {
            logger.error("Usage: GenesisBlockGenerator <number-of-users>");
        }

        int numUsers = Integer.parseInt(args[0]);
        List<Address> eoaList = generateClientAddresses(numUsers);
        if (eoaList == null) return;

        // set the world and EVM
        SimpleWorld world = new SimpleWorld();
        SmartContractExecutor executor = new SmartContractExecutor(world);

        // create client accounts (i.e. EOA accounts) from their addresses
        for (Address address : eoaList) {
            world.getOrCreate(address);
        }

        // assume client with id zero is the owner of both contracts
        Address ownerAddr = eoaList.get(0);

        // deploy AccessControl.sol
        Address blacklistAddr = executor.deployContract(accessControlBytecode, ownerAddr, 0);
        executor.setBlacklistContract(world.getAccount(blacklistAddr));

        // deploy ISTCoin.sol using AccessControl.sol as AccessControl interface
        String paddedBlacklistAddr = DataUtils.padHexString(blacklistAddr.toHexString());
        Bytes tokenBytecode = Bytes.fromHexString(fungibleTokenBytecode.toHexString() + paddedBlacklistAddr);
        Address tokenAddr = executor.deployContract(tokenBytecode, ownerAddr, 1);
        executor.setTokenContract(world.getAccount(tokenAddr));

        // transfer 1000 tokens to each account
        for (Address address : eoaList) {
            executor.transfer(ownerAddr, address, 1000);
        }

        Block genesisBlock = new Block(world, blacklistAddr, tokenAddr, null);

        // DEBUG
        // genesisBlock.debugState();

        saveToFile(genesisBlock);
        logger.info("Genesis block generated successfully!");
    }



    private static List<Address> generateClientAddresses(int numUsers) {
        List<Address> addresses = new ArrayList<>();
        for (int i = 0; i < numUsers; i++) {
            String publicKeyPath = getPublicKeyPath(i);
            PublicKey publicKey;
            try {
                publicKey = RSAKeyReader.readPublicKey(publicKeyPath);
                addresses.add(AddressGenerator.generateAddress(publicKey));
            } catch (Exception e) {
                logger.error("Error reading public key and generating address", e);
                return null;
            }
        }
        return addresses;
    }

    private static String getPublicKeyPath(int userId) {
        return publicKeysDir + "client" + userId + "_public.key";
    }

    private static void saveToFile(Block genesisBlock) throws IOException {
        // JSON without indentation
        String jsonString = genesisBlock.toJson();

        // Improve indentation
        ObjectMapper objectMapper = new ObjectMapper();
        JsonNode jsonNode = objectMapper.readTree(jsonString);
        String indentedJson = objectMapper.writerWithDefaultPrettyPrinter().writeValueAsString(jsonNode);

        // DEBUG: obtain world from genesis block json
        // Block.fromJson(indentedJson).debugState();

        try (FileOutputStream fos = new FileOutputStream(genesisBlockPath)) {
            fos.write(indentedJson.getBytes());
            logger.info("Saved genesis block:\n{}", indentedJson);
        }

        // DEBUG: obtain world from genesis block file
        // Block block = Block.loadFromFile(genesisBlockPath);
        // block.debugState();

        // DEBUG: test smart contract execution
        // SmartContractExecutor executor = new SmartContractExecutor(block.getWorld(), block.getBlacklistAddress(), block.getTokenAddress());
    }
}
